import React, {useState,useMemo} from 'react';
import Counter from "./components/Counter";
import ClassCounter from './components/ClassCounter';
import './styles/App.css';
import PostItem from './components/PostItem';
import PostList from './components/PostList';
import MyButton from './components/UI/button/MyButton';
import MyInput from './components/UI/input/MyInput';
import PostForm from './components/PostForm';
import MySelect from './components/select/MySelect';
function App() {
  const [posts,setPosts] = useState([
    {id:1,title:'JavaScriptbb',body:'Description'},
    {id:2,title:'JavaScriptaa',body:'Description'},
    {id:3,title:'JavaScriptdd',body:'Description'},

  ])
  const [selectedSort, setSelectedSort]= useState('')
  const [searchQuery, setSearchQuery] = useState('')

  const sortedPosts = useMemo(() => {
    if(selectedSort){
      return [...posts].sort((a,b) => a[selectedSort].localeCompare(b[selectedSort]))

    }
    return posts;
  },[selectedSort,posts])

  const sorttedAndSearchPosts = useMemo( () => {
    return sortedPosts.filter(post => post.title.toLowercase().includes(searchQuery))
  }, [searchQuery,sortedPosts])


  const createPost = (newPost) =>{
    setPosts([...posts,newPost])
  }

  const removePost = (post) =>{
    setPosts(posts.filter(p=> p.id !== post.id))
  }

  const sortPosts = (sort) => {
    setSelectedSort(sort);
  }
  return (
    <div className="App">
        <PostForm create={createPost}/>
        <hr style={{margin:'15px 0'}}/>

        <MyInput
          value={searchQuery}
          onChange={e=> setSearchQuery(e.targeet.value)}
          placeholder="Search..."
        />
          <MySelect value={selectedSort} onChange={sortPosts}
            defaultValue="Sort"
            options={[
              {value:'title', name: 'Name sort'},
              {value:'body', name: 'Description sort'},

            ]}
          />

        {sorttedAndSearchPosts.length
          ?<PostList remove={removePost} posts={sorttedAndSearchPosts} title="Spisok postov 1"/>  
          :<h1 style = {{textAlign:'center'}}>No posts!</h1>}
    </div>
  );
}

export default App;
